"use strict";(self.webpackChunkkooku_log=self.webpackChunkkooku_log||[]).push([[5002],{3865:(t,n,e)=>{e.r(n),e.d(n,{assets:()=>c,contentTitle:()=>a,default:()=>d,frontMatter:()=>s,metadata:()=>o,toc:()=>l});const o=JSON.parse('{"id":"books/atomic-habit/11","title":"Chapter 11. 1\ub9cc \uc2dc\uac04\uc758 \ubc95\uce59\uc740 \ud2c0\ub838\ub2e4.","description":"\ud50c\ub85c\ub9ac\ub2e4 \ub300\ud559\uad50\uc758 \uc81c\ub9ac \uc728\uc2a4\ub9cc \uad50\uc218\ub294 \uc601\ud654\uc0ac\uc9c4 \uc218\uc5c5 \uccab\ub0a0, \ud559\uc0dd\ub4e4\uc744 \ub450 \uc9d1\ub2e8\uc73c\ub85c \ub098\ub234\ub2e4. \uac15\uc758\uc2e4 \uc67c\ucabd\uc5d0 \uc788\ub358 \ud559\uc0dd\ub4e4\uc740 \'\uc591\uc801 \uc9d1\ub2e8\'\uc774\ub77c\uace0 \uc774\ub984 \ubd99\uc5ec\uc92c\uace0, \uc774\ub4e4\uc740 \uc218\ud589\ud55c \uacfc\uc81c\uc758 \uc591\ub9cc\uc73c\ub85c \ud3c9\uac00\ub97c \ubc1b\uae30\ub85c \ud588\ub2e4. \uac15\uc758 \ub9c8\uc9c0\ub9c9 \ub0a0 \uc728\uc2a4\ub9cc\uc740 \uc774 \ud559\uc0dd\ub4e4\uc774 \uc81c\ucd9c\ud55c \uc0ac\uc9c4\uc758 \'\uc591\'\ub9cc \ubd24\ub2e4. \uc989, \uacfc\uc81c \uc0ac\uc9c4 100\uc7a5\uc744 \uc81c\ucd9c\ud558\uba74 A, 90\uc7a5\uc744 \uc81c\ucd9c\ud558\uba74 B, 80\uc7a5\uc744 \uc81c\ucd9c\ud558\uba74 C \uc774\ub7f0 \uc2dd\uc73c\ub85c \ud559\uc810\uc744 \ub9e4\uacbc\ub2e4.","source":"@site/docs/books/atomic-habit/11.md","sourceDirName":"books/atomic-habit","slug":"/books/atomic-habit/11","permalink":"/docs/books/atomic-habit/11","draft":false,"unlisted":false,"editUrl":"https://github.com/kooku0/kooku0.github.io/edit/main/docs/books/atomic-habit/11.md","tags":[],"version":"current","frontMatter":{"title":"Chapter 11. 1\ub9cc \uc2dc\uac04\uc758 \ubc95\uce59\uc740 \ud2c0\ub838\ub2e4."},"sidebar":"books","previous":{"title":"Chapter 10. \ub098\uc05c \uc2b5\uad00\ub3c4 \uc990\uac81\uac8c \uace0\uce60 \uc218 \uc788\uc744\uae4c","permalink":"/docs/books/atomic-habit/10"},"next":{"title":"Chapter 12. \uc6ec\ub9cc\ud558\uba74 \uc27d\uac8c \uac11\uc2dc\ub2e4","permalink":"/docs/books/atomic-habit/12"}}');var i=e(23420),r=e(38906);const s={title:"Chapter 11. 1\ub9cc \uc2dc\uac04\uc758 \ubc95\uce59\uc740 \ud2c0\ub838\ub2e4."},a="Chapter 11. 1\ub9cc \uc2dc\uac04\uc758 \ubc95\uce59\uc740 \ud2c0\ub838\ub2e4.",c={},l=[{value:"\ub9ce\uc774 \ud574\uc57c \ud560\uae4c, \uc624\ub798 \ud574\uc57c \ud560\uae4c",id:"\ub9ce\uc774-\ud574\uc57c-\ud560\uae4c-\uc624\ub798-\ud574\uc57c-\ud560\uae4c",level:2},{value:"Summary",id:"summary",level:2}];function h(t){const n={h1:"h1",h2:"h2",header:"header",li:"li",p:"p",ul:"ul",...(0,r.R)(),...t.components};return(0,i.jsxs)(i.Fragment,{children:[(0,i.jsx)(n.header,{children:(0,i.jsx)(n.h1,{id:"chapter-11-1\ub9cc-\uc2dc\uac04\uc758-\ubc95\uce59\uc740-\ud2c0\ub838\ub2e4",children:"Chapter 11. 1\ub9cc \uc2dc\uac04\uc758 \ubc95\uce59\uc740 \ud2c0\ub838\ub2e4."})}),"\n",(0,i.jsx)(n.p,{children:"\ud50c\ub85c\ub9ac\ub2e4 \ub300\ud559\uad50\uc758 \uc81c\ub9ac \uc728\uc2a4\ub9cc \uad50\uc218\ub294 \uc601\ud654\uc0ac\uc9c4 \uc218\uc5c5 \uccab\ub0a0, \ud559\uc0dd\ub4e4\uc744 \ub450 \uc9d1\ub2e8\uc73c\ub85c \ub098\ub234\ub2e4. \uac15\uc758\uc2e4 \uc67c\ucabd\uc5d0 \uc788\ub358 \ud559\uc0dd\ub4e4\uc740 '\uc591\uc801 \uc9d1\ub2e8'\uc774\ub77c\uace0 \uc774\ub984 \ubd99\uc5ec\uc92c\uace0, \uc774\ub4e4\uc740 \uc218\ud589\ud55c \uacfc\uc81c\uc758 \uc591\ub9cc\uc73c\ub85c \ud3c9\uac00\ub97c \ubc1b\uae30\ub85c \ud588\ub2e4. \uac15\uc758 \ub9c8\uc9c0\ub9c9 \ub0a0 \uc728\uc2a4\ub9cc\uc740 \uc774 \ud559\uc0dd\ub4e4\uc774 \uc81c\ucd9c\ud55c \uc0ac\uc9c4\uc758 '\uc591'\ub9cc \ubd24\ub2e4. \uc989, \uacfc\uc81c \uc0ac\uc9c4 100\uc7a5\uc744 \uc81c\ucd9c\ud558\uba74 A, 90\uc7a5\uc744 \uc81c\ucd9c\ud558\uba74 B, 80\uc7a5\uc744 \uc81c\ucd9c\ud558\uba74 C \uc774\ub7f0 \uc2dd\uc73c\ub85c \ud559\uc810\uc744 \ub9e4\uacbc\ub2e4.\n\ubc18\ub300\ub85c \uac15\uc758\uc2e4 \uc624\ub978\ucabd\uc5d0 \uc788\ub358 \ud559\uc0dd\ub4e4\uc740 '\uc9c8\uc801 \uc9d1\ub2e8'\uc73c\ub85c \uc774\ub984 \ubd99\uc5ec\uc84c\ub2e4. \uc774\ub4e4\uc740 \uacfc\uc81c\uc758 '\uc9c8'\ub9cc \ud3c9\uac00\ubc1b\uc558\ub2e4. \ud55c \ud559\uae30 \ub3d9\uc548 \uc624\uc9c1 \ud55c \uc7a5\uc758 \uc0ac\uc9c4\ub9cc \uacfc\uc81c\ub85c \uc81c\ucd9c\ud588\ub294\ub370, \uc774 \uc0ac\uc9c4 \ud55c \uc7a5\uc758 \uc9c8\uc801 \uc644\uc131\ub3c4\uc5d0 \ub530\ub77c \ud559\uc810\uc744 \ubc1b\uc558\ub2e4.\n\ud559\uae30 \ub9d0\uc5d0 \uc728\uc2a4\ub9cc\uc740 \uac00\uc7a5 \uc644\uc131\ub3c4 \ub192\uc740 \uc0ac\uc9c4\ub4e4\uc774 \uc591\uc801 \uc9d1\ub2e8\uc5d0\uc11c \ub098\uc654\ub2e4\ub294 \uac83\uc744 \ubcf4\uace0\ub294 \ub180\ub77c\uc6c0\uc744 \uae08\ud560 \uc218 \uc5c6\uc5c8\ub2e4. \ud55c \ud559\uae30 \ub3d9\uc548 \uc774 \ud559\uc0dd\ub4e4\uc740 \uc218\uc5c6\uc774 \uc0ac\uc9c4\uc744 \ucc0d\uace0, \uad6c\ub3c4\uc640 \uc870\uba85\uc744 \uc2e4\ud5d8\ud574\ubcf4\uace0, \ub2e4\uc591\ud55c \uc778\ud654 \ubc29\ubc95\uc744 \ud14c\uc2a4\ud2b8\ud574\ubcf4\uba74\uc11c \uc218\ub9ce\uc740 \uc2e4\uc218\ub97c \ud1b5\ud574 \ubc30\uc6cc\ub098\uac14\ub2e4. \uc218\ubc31 \uc7a5\uc758 \uc0ac\uc9c4\uc744 \ub9cc\ub4e4\uc5b4\ub0b4\ub294 \uacfc\uc815\uc5d0\uc11c \uc774\ub4e4\uc758 \uae30\uc220\uc774 \ub180\ub77c\uc6b8 \uc815\ub3c4\ub85c \ud5a5\uc0c1\ub410\ub2e4. \ubc18\ub300\ub85c \uc9c8\uc801 \uc9d1\ub2e8\uc740 \uc0ac\uc9c4\uc758 \uc644\uc131\ub3c4\uc5d0\ub9cc \ub9e4\ub2ec\ub838\ub2e4. \uacb0\uad6d \uc774\ub4e4\uc740 \uc785\uc99d\ub418\uc9c0 \uc54a\uc740 \uc774\ub860\ub4e4\uc774\ub098 \ubcf4\ud1b5 \uc218\uc900\ubc16\uc5d0 \uc548 \ub418\ub294 \uc0ac\uc9c4\uc5d0\ub294 \ub178\ub825\uc744 \uae30\uc6b8\uc774\uc9c0 \uc54a\uc558\ub2e4."}),"\n",(0,i.jsx)(n.p,{children:"\uc774\ub807\ub4ef \uc123\ubd80\ub974\uac8c \uc811\uadfc\ud558\uba74 \ubcc0\ud654\ub97c \uc704\ud55c \ucd5c\uc120\uc758 \ubc29\ubc95\uc744 \ucc3e\uc544\ub0b4\uc9c0 \ubabb\ud558\uace0 \uc218\ub839\uc5d0 \ube60\uc9c0\uac8c \ub41c\ub2e4. \uc608\ub97c \ub4e4\uba74 \uc0b4\uc744 \ube7c\ub294 \uac00\uc7a5 \ube60\ub978 \ubc29\ubc95, \uadfc\uc721\uc744 \ud0a4\uc6b0\ub294 \ucd5c\uace0\uc758 \ud504\ub85c\uadf8\ub7a8, \uc644\ubcbd\ud55c \ubd80\uc5c5 \uc544\uc774\ub514\uc5b4\ub9cc\uc744 \ucc3e\ub294 \uac83\uc774\ub2e4. \ud589\ub3d9\uc744 \ucde8\ud558\uc9c0\ub294 \uc54a\uace0 \ucd5c\uc120\uc758 \uc811\uadfc\ubc95\uc744 \uc0dd\uac01\ud574\ub0b4\ub294 \ub370\ub9cc \ubab0\ub450\ud55c\ub2e4.\n\uc774 \ub9d0\uc740 motion\uacfc action \uc0ac\uc774\uc758 \ucc28\uc774\ub97c \ub9d0\ud574\uc900\ub2e4. \uc774 \ub450 \uac00\uc9c0 \uac1c\ub150\uc740 \uc720\uc0ac\ud558\uac8c \ub4e4\ub9ac\uc9c0\ub9cc \uacb0\ucf54 \uac19\uc9c0 \uc54a\ub2e4. motion\uc740 \uacc4\ud68d\uc744 \uc138\uc6b0\uace0 \uc804\ub7b5\uc744 \ud655\ub9bd\ud558\uace0 \ubc30\uc6b0\ub294 \uac83\uc774\ub2e4. \uc88b\uc740 \uc77c\uc774\uc9c0\ub9cc \uacb0\uacfc\ub97c \ub9cc\ub4e4\uc5b4\ub0b4\uc9c0\ub294 \uc54a\ub294\ub2e4.\n\ubc18\ub300\ub85c action\uc740 \ud589\uc704\ub85c\uc11c \uacb0\uacfc\ub97c \ub3c4\ucd9c\ud55c\ub2e4. \uc608\ub97c \ub4e4\uc5b4 \ub0b4\uac00 \uc4f0\uace0\uc790 \ud558\ub294 \uae30\uace0\ubb38\ub4e4\uc5d0 \ub300\ud574 20\uc5ec \uac00\uc9c0\uc758 \uc544\uc774\ub514\uc5b4\ub97c \ub0c8\ub2e4\uba74 \uc774\uac83\uc740 motion\uc774\ub2e4. \uadf8\ub7ec\ub098 \uc2e4\uc81c\ub85c \uc549\uc544\uc11c \uae30\uace0\ubb38\uc744 \uc4f0\uace0 \uc788\ub2e4\uba74 \uc774\uac83\uc740 action\uc774\ub2e4. \ub354 \ub098\uc740 \ub2e4\uc774\uc5b4\ud2b8 \uacc4\ud68d\uc744 \uac80\uc0c9\ud558\uac70\ub098 \uadf8 \uc8fc\uc81c\uc5d0 \ub300\ud574 \ucc45\uc744 \uba87 \uad8c \uc77d\ub294 \uac83\uc740 \ub3d9\uc791\uc774\ub2e4. \ud558\uc9c0\ub9cc \uac74\uac15\ud55c \uc74c\uc2dd\uc744 \uc2e4\uc81c\ub85c \uba39\ub294\ub2e4\uba74 \uc774\uac83\uc740 \uc2e4\ud589\uc774\ub2e4.\n\ub3d9\uc791\uc740 \uc720\uc6a9\ud558\uc9c0\ub9cc \uacb0\ucf54 \uadf8 \uc790\uccb4\ub85c \uacb0\uacfc\ub97c \ub9cc\ub4e4\uc5b4\ub0b4\uc9c0\ub294 \uc54a\ub294\ub2e4.\n\ub3d9\uc791\uc774 \uacb0\uacfc\ub85c \uc774\uc5b4\uc9c0\uc9c0 \uc54a\ub294\ub2e4\uba74 \uc65c \uc6b0\ub9ac\ub294 \uadf8\ub807\uac8c \ud589\ub3d9\ud560\uae4c? \uc2e4\uc81c\ub85c \uacc4\ud68d\uc774 \ud544\uc694\ud558\uac70\ub098 \ub354 \ub9ce\uc774 \ubc30\uc6cc\uc57c \ud558\uae30 \ub54c\ubb38\uc5d0 \ub3d9\uc791\uc774 \ud544\uc694\ud560 \ub54c\ub3c4 \uc788\ub2e4. \ud558\uc9c0\ub9cc \uc6b0\ub9ac\ub294 \uc885\uc885 \uc2e4\ud328\ud560 \uc704\ud5d8 \uc5c6\uc774 \uadf8 \uacfc\uc815\uc744 \uacaa\ub294 \ub4ef\ud55c \uae30\ubd84\uc744 \ub290\ub084 \uc218 \uc788\uae30 \ub54c\ubb38\uc5d0 '\uc6c0\uc9c1\uc774\uae30\ub9cc' \ud55c\ub2e4."}),"\n",(0,i.jsx)(n.p,{children:"\uc6b0\ub9ac \ub300\ubd80\ubd84\uc740 \ube44\ud310\uc744 \ud53c\ud558\ub294 \ub370 \uc120\uc218\ub4e4\uc774\ub2e4. \uc2e4\ud328\ud558\uac70\ub098 \uc0ac\ub78c\ub4e4\uc758 \ud3c9\uac00\ub97c \ubc1b\ub294 \uac74 \uae30\ubd84 \uc88b\uc740 \uc77c\uc740 \uc544\ub2c8\ubbc0\ub85c \uadf8\ub7f0 \uc77c\uc774 \ubc1c\uc0dd\ud560 \uc0c1\ud669\uc744 \ud53c\ud558\ub294 \uac83\uc774\ub2e4. \uc774\uac83\uc774 \uc6b0\ub9ac\uac00 \uc2e4\uc81c\ub85c \uc2e4\ud589\ud558\uae30\ubcf4\ub2e4\ub294 \ub3d9\uc791 \uc815\ub3c4\uae4c\uc9c0\ub9cc \ud558\ub294 \uac00\uc7a5 \ud070 \uc774\uc720\uae30\ub3c4 \ud558\ub2e4. \uc2e4\ud328\ud558\uace0 \uc2f6\uc9c0 \uc54a\uace0, \ud558\ub354\ub77c\ub3c4 \uac00\ub2a5\ud558\uba74 \ub098\uc911\uc73c\ub85c \ubbf8\ub8e8\uace0 \uc2f6\uc740 \uac83\uc774\ub2e4.\n\ub3d9\uc791\uc740 \uc27d\ub2e4. \uc544\uc9c1 \uc790\uc2e0\uc774 \uadf8 \uacfc\uc815 \uc911\uc5d0 \uc788\ub2e4\uace0 \ub290\ub07c\uac8c \ud574\uc900\ub2e4. \ub3d9\uc791\uc740 \ubb54\uac00\ub97c \ud588\ub2e4\ub294 \ub290\ub08c\uc744 \uc900\ub2e4. \ud558\uc9c0\ub9cc \uc2e4\uc81c\ub85c\ub294 \ubb54\uac00\ub97c \ud558\ub294 \uc900\ube44\ub9cc \ud55c \uac83 \ubfd0\uc774\ub2e4. \uc900\ube44\uac00 '\ubbf8\ub8e8\uae30'\uc758 \ub610 \ub2e4\ub978 \ud615\ud0dc\uac00 \ub3fc\uc11c\ub294 \uc548\ub41c\ub2e4. \ubb34\uc5c7\uc774\ub4e0 \uc2e4\uc81c\ub85c \ubcc0\ud654\ud558\ub294 \uac8c \uc911\uc694\ud558\ub2e4. \uc6b0\ub9ac\ub294 \ud55c\ub0b1 \uc900\ube44\ub9cc \ud558\ub294 \uac83\uc744 \ubc14\ub77c\uc9c0 \uc54a\ub294\ub2e4. \uc5f0\uc2b5\uc744 \ubc14\ub780\ub2e4.\n\uc5b4\ub5a4 \uc2b5\uad00\uc5d0 \ud1b5\ub2ec\ud558\ub824\uba74 \uac00\uc7a5 \uc911\uc694\ud55c \uac74 '\ubc18\ubcf5'\uc774\ub2e4. \uc644\uc131\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub2e4. \uc0c8\ub85c \uc775\ud600\uc57c \ud560 \uc2b5\uad00\uc758 \uba74\uba74\uc744 \uadf8\ub824 \ubcfc \ud544\uc694\ub294 \uc5c6\ub2e4. \uadf8\uac83\uc744 \uc5f0\uc2b5\ud558\uae30\ub9cc \ud558\uba74 \ub41c\ub2e4."}),"\n",(0,i.jsx)(n.h2,{id:"\ub9ce\uc774-\ud574\uc57c-\ud560\uae4c-\uc624\ub798-\ud574\uc57c-\ud560\uae4c",children:"\ub9ce\uc774 \ud574\uc57c \ud560\uae4c, \uc624\ub798 \ud574\uc57c \ud560\uae4c"}),"\n",(0,i.jsx)(n.p,{children:"\uc2b5\uad00\uc740 \ubc18\ubcf5\ub41c \ud589\ub3d9\uc744 \ud1b5\ud574 \uc810\ucc28\uc801\uc73c\ub85c \uc790\ub3d9\ud654\ub418\uba74\uc11c \ub9cc\ub4e4\uc5b4\uc9c4\ub2e4. \uc5b4\ub5a4 \ud589\ub3d9\uc744 \ubc18\ubcf5\ud560\uc218\ub85d, \ub1cc\ub294 \uadf8 \ud589\ub3d9\uc744 \ud558\ub294 \ub370 \ub354 \ud6a8\uc728\uc801\uc778 \uad6c\uc870\ub85c \ubcc0\ud654\ud55c\ub2e4. \uc2e0\uacbd\uacfc\ud559\uc790\ub4e4\uc740 \uc774\ub97c '\uc7a5\uae30\uc801 \uac15\ud654'\ub77c\uace0 \ubd80\ub974\ub294\ub370, \ucd5c\uadfc\uc5d0 \ud328\ud134\ud654\ub41c \ud589\ub3d9\ub4e4\uc744 \uae30\ubc18\uc73c\ub85c \ub1cc\uc5d0\uc11c \ub274\ub7f0\ub4e4\uc758 \uc5f0\uacb0\uc774 \uac15\ud654\ub418\ub294 \uac83\uc744 \ub9d0\ud55c\ub2e4.\n\uc5b4\ub5a4 \ud589\ub3d9\uc744 \ubc18\ubcf5\ud558\ub294 \uc21c\uac04\ub9c8\ub2e4 \uc6b0\ub9ac\ub294 \uadf8 \uc2b5\uad00\uacfc \uc5f0\uad00\ub41c \ud2b9\uc815\ud55c \uc2e0\uacbd\ud559\uc801 \ud68c\ub85c\ub97c \ud65c\uc131\ud654\ud55c\ub2e4. \uc989, \ub2e8\uc21c\ud55c \ubc18\ubcf5\uc774\ub77c \ud560\uc9c0\ub77c\ub3c4 \uc0c8\ub85c\uc6b4 \uc2b5\uad00\uc744 \uccb4\ud654\ud558\ub294 \uac00\uc7a5 \uc911\uc694\ud55c \ub2e8\uacc4\ub77c\ub294 \ub9d0\uc774\ub2e4.\n\uc5c4\uccad\ub09c \uc591\uc758 \uc0ac\uc9c4\uc744 \ucc0d\uc740 \ud559\uc0dd\ub4e4\uc740 \uae30\uc220\uc774 \ub298\uace0, \uc644\ubcbd\ud55c \uc0ac\uc9c4\uc744 \ub9cc\ub4e4\uc5b4\ub0b4\ub294 \ub370\ub9cc \ubab0\ub450\ud55c \ud559\uc0dd\ub4e4\uc740 \uae30\uc220\uc774 \ub298\uc9c0 \ubabb\ud55c \uc774\uc720\ub3c4 \uc774 \ub54c\ubb38\uc774\ub2e4.\n\ubaa8\ub4e0 \uc2b5\uad00\uc740 \ub178\ub825\uc774 \ud544\uc694\ud55c \uc5f0\uc2b5\uc5d0\uc11c \uc790\ub3d9\uc801\uc778 \ud589\uc704\ub85c \ub118\uc5b4\uac00\ub294 \uc720\uc0ac\ud55c \uada4\uc801\uc744 \uadf8\ub9ac\ub294\ub370, \uc774 \uacfc\uc815\uc744 '\uc790\ub3d9\ud654'\ub77c\uace0 \ud55c\ub2e4. \uc790\ub3d9\ud654\ub294 \uac01 \ub2e8\uacc4\ub97c \ub5a0\uc62c\ub9ac\uc9c0 \uc54a\uace0\ub3c4 \ud589\ub3d9\uc744 \uc218\ud589\ud558\ub294 \ub2a5\ub825\uc73c\ub85c, \ud589\ub3d9\uc774 \ubb34\uc758\uc2dd\uc5d0 \uc0c8\uaca8\uc9c8 \ub54c \uc77c\uc5b4\ub09c\ub2e4."}),"\n",(0,i.jsx)(n.p,{children:"\uc2b5\uad00\uc740 '\uc2dc\uac04'\uc774 \uc544\ub2c8\ub77c '\ud69f\uc218'\uc5d0 \uae30\ubc18\ud574 \ud615\uc131\ub41c\ub2e4. \uc6b0\ub9ac\uc758 \ud604\uc7ac \uc2b5\uad00\uc740 \uc218\ubc31, \uc218\ucc9c \ubc88 \ubc18\ubcf5\ud558\ub294 \uacfc\uc815\uc5d0\uc11c \ub0b4\uc7ac\ud654\ub41c \uac83\uc774\ub2e4. \uc0c8\ub85c\uc6b4 \uc2b5\uad00 \uc5ed\uc2dc \uadf8\ub9cc\ud07c\uc758 \ubc18\ubcf5\uc774 \ud544\uc694\ud558\ub2e4. \ud589\ub3d9\uc774 \uc815\uc2e0 \uc18d\uc5d0 \uc644\uc804\ud788 \ub0b4\uc7a5\ub418\uace0, \uc2b5\uad00 \ud55c\uacc4\uc120\uc744 \ub118\uc5b4\uc124 \ub54c\uae4c\uc9c0 \uc131\uacf5\uc801\uc778 \uc2dc\ub3c4\ub4e4\uc744 \ucda9\ubd84\ud788 \uc5ee\uc5b4\ub0b4\uc57c \ud55c\ub2e4.\n\ub530\ub77c\uc11c \uc2b5\uad00\uc744 \uc790\ub3d9\ud654\ud558\uae30 \uc704\ud574 \uc5bc\ub9c8\ub098 \ub9ce\uc740 \uc2dc\uac04\uc744 \ub4e4\uc600\ub290\ub0d0\ub294 \uc911\uc694\ud558\uc9c0 \uc54a\ub2e4. \uadf8 \uacfc\uc815\uc774 \uc774\ub904\uc9c0\ub294 \ub370 \ud544\uc694\ud55c \ub9cc\ud07c \uadf8 \ud589\ub3d9\uc744 \ucde8\ud588\ub290\ub0d0\uac00 \uc911\uc694\ud558\ub2e4."}),"\n",(0,i.jsx)(n.h2,{id:"summary",children:"Summary"}),"\n",(0,i.jsxs)(n.ul,{children:["\n",(0,i.jsx)(n.li,{children:"\uac00\uc7a5 \ud6a8\uc728\uc801\uc778 \ud559\uc2b5 \ud615\ud0dc\ub294 \uc2e4\ud589\uc774\ub2e4. \uacc4\ud68d \uc138\uc6b0\uae30\uac00 \uc544\ub2c8\ub2e4."}),"\n",(0,i.jsx)(n.li,{children:"\uc2e4\ud589\ud558\ub294 \ub370 \ucd08\uc810\uc744 \ub9de\ucdb0\ub77c. \uc6c0\uc9c1\uc784\uc5d0 \ub9de\ucd94\uc9c0 \ub9c8\ub77c."}),"\n",(0,i.jsx)(n.li,{children:"\uc2b5\uad00\uc740 \ubc18\ubcf5\ub41c \ud589\ub3d9\uc744 \ud1b5\ud574 \uc810\ucc28\uc801\uc73c\ub85c \uc790\ub3d9\ud654\ub418\uba74\uc11c \ub9cc\ub4e4\uc5b4\uc9c4\ub2e4."}),"\n",(0,i.jsx)(n.li,{children:"\uc2b5\uad00\uc744 \ud615\uc131\ud558\ub294 \ub370 \ub4e4\uc778 \uc2dc\uac04\ubcf4\ub2e4 \uadf8 \uc2b5\uad00\uc744 \uc2e4\ud589\ud55c \ud69f\uc218\uac00 \ub354 \uc911\uc694\ud558\ub2e4."}),"\n"]})]})}function d(t={}){const{wrapper:n}={...(0,r.R)(),...t.components};return n?(0,i.jsx)(n,{...t,children:(0,i.jsx)(h,{...t})}):h(t)}},38906:(t,n,e)=>{e.d(n,{R:()=>s,x:()=>a});var o=e(36672);const i={},r=o.createContext(i);function s(t){const n=o.useContext(r);return o.useMemo((function(){return"function"==typeof t?t(n):{...n,...t}}),[n,t])}function a(t){let n;return n=t.disableParentContext?"function"==typeof t.components?t.components(i):t.components||i:s(t.components),o.createElement(r.Provider,{value:n},t.children)}}}]);